/**
 * 订阅套餐Tab页面
 * 显示用户当前订阅和可用套餐
 */
import React, { useState, useEffect } from 'react';
import { Card, CardBody, Button, Chip, Spinner, Modal, ModalContent, ModalHeader, ModalBody, ModalFooter } from '@heroui/react';
import { motion } from 'framer-motion';
import { Package, Star, Crown, AlertCircle, CheckCircle, Clock, CreditCard, QrCode } from 'lucide-react';
import { packageUserApi, orderUserApi } from '../../../services/userApi';
import QRCodeGenerator from 'qrcode-generator';

interface PackageInfo {
  id: number;
  package_name: string;
  category: string;
  price: number;
  duration: number;
  introduce: string;
  level: string;
  priority: number;
  remarks: string;
  status: number;
}

interface OrderInfo {
  success: boolean;
  trade_no: string;
  order_id: string;
  payment_url: string | null;
  qr_code: string;
  channel: string;
  pay_type: string;
}

export const SubscriptionTab: React.FC = () => {
  const [packages, setPackages] = useState<PackageInfo[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string>('');
  const [selectedPackage, setSelectedPackage] = useState<PackageInfo | null>(null);
  const [orderInfo, setOrderInfo] = useState<OrderInfo | null>(null);
  const [orderLoading, setOrderLoading] = useState(false);
  const [paymentModal, setPaymentModal] = useState(false);
  const [paymentStatus, setPaymentStatus] = useState<'pending' | 'checking' | 'success' | 'failed'>('pending');
  const [checkInterval, setCheckInterval] = useState<NodeJS.Timeout | null>(null);

  // 获取套餐列表
  const fetchPackages = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await packageUserApi.getPackages();

      if (response.code === 20000) {
        setPackages(response.data || []);
      } else {
        setError(response.msg || '获取套餐列表失败');
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : '网络错误');
    } finally {
      setLoading(false);
    }
  };

  // 创建订单
  const createOrder = async (packageId: number) => {
    try {
      setOrderLoading(true);
      const response = await orderUserApi.createOrder(packageId);

      if (response.code === 20000) {
        setOrderInfo(response.data);
        setPaymentModal(true);
        setPaymentStatus('pending');
        startPaymentStatusCheck(response.data.order_id);
      } else {
        alert(response.msg || '创建订单失败');
      }
    } catch (err) {
      alert(err instanceof Error ? err.message : '网络错误');
    } finally {
      setOrderLoading(false);
    }
  };

  // 开始检查支付状态
  const startPaymentStatusCheck = (orderId: string) => {
    if (checkInterval) {
      clearInterval(checkInterval);
    }

    const interval = setInterval(async () => {
      try {
        setPaymentStatus('checking');
        const response = await orderUserApi.getPayStatus(orderId);

        if (response.code === 20000) {
          setPaymentStatus('success');
          clearInterval(interval);
          setTimeout(() => {
            setPaymentModal(false);
            fetchPackages(); // 刷新套餐列表
          }, 3000);
        } else if (response.code === 20001) {
          setPaymentStatus('pending');
        } else {
          setPaymentStatus('failed');
          clearInterval(interval);
        }
      } catch (err) {
        console.error('检查支付状态失败:', err);
      }
    }, 3000); // 每3秒检查一次

    setCheckInterval(interval);
  };

  // 生成二维码
  const generateQRCode = (text: string): string => {
    const qr = QRCodeGenerator(0, 'M');
    qr.addData(text);
    qr.make();
    return qr.createDataURL(8, 4);
  };

  // 获取套餐等级样式
  const getPackageLevelStyle = (level: string) => {
    switch (level.toLowerCase()) {
      case 'base':
        return { 
          icon: Package, 
          color: 'default' as const, 
          bgColor: 'bg-default/10',
          borderColor: 'border-default-200'
        };
      case 'plus':
        return { 
          icon: Star, 
          color: 'primary' as const, 
          bgColor: 'bg-primary/10',
          borderColor: 'border-primary-200'
        };
      case 'pro':
      case 'premium':
        return { 
          icon: Crown, 
          color: 'warning' as const, 
          bgColor: 'bg-warning/10',
          borderColor: 'border-warning-200'
        };
      default:
        return { 
          icon: Package, 
          color: 'default' as const, 
          bgColor: 'bg-default/10',
          borderColor: 'border-default-200'
        };
    }
  };

  // 关闭支付弹窗
  const closePaymentModal = () => {
    if (checkInterval) {
      clearInterval(checkInterval);
      setCheckInterval(null);
    }
    setPaymentModal(false);
    setOrderInfo(null);
    setPaymentStatus('pending');
  };

  useEffect(() => {
    fetchPackages();

    // 清理定时器
    return () => {
      if (checkInterval) {
        clearInterval(checkInterval);
      }
    };
  }, []);

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      className="space-y-6"
    >
      {/* 页面标题 */}
      <div className="flex items-center gap-3">
        <div className="w-8 h-8 bg-primary/10 rounded-lg flex items-center justify-center">
          <Package size={20} className="text-primary" />
        </div>
        <div>
          <h1 className="text-2xl font-bold text-default-900">订阅套餐</h1>
          <p className="text-sm text-default-500 mt-1">选择适合您的套餐方案</p>
        </div>
      </div>

      {/* 加载状态 */}
      {loading && (
        <Card>
          <CardBody className="p-6">
            <div className="flex items-center justify-center py-12">
              <Spinner size="lg" color="primary" />
              <span className="ml-3 text-default-600">加载中...</span>
            </div>
          </CardBody>
        </Card>
      )}

      {/* 错误状态 */}
      {error && (
        <Card>
          <CardBody className="p-6">
            <div className="flex items-start gap-4 p-6 bg-danger/10 rounded-lg">
              <AlertCircle size={20} className="text-danger flex-shrink-0 mt-1" />
              <div>
                <h3 className="font-semibold text-danger mb-2">加载失败</h3>
                <p className="text-default-600 text-sm">{error}</p>
                <button
                  onClick={fetchPackages}
                  className="mt-3 text-sm text-primary hover:text-primary-600 underline"
                >
                  重试
                </button>
              </div>
            </div>
          </CardBody>
        </Card>
      )}

      {/* 套餐列表 */}
      {!loading && !error && packages.length > 0 && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {packages.map((pkg) => {
            const levelStyle = getPackageLevelStyle(pkg.level);
            const IconComponent = levelStyle.icon;
            const isRecommended = pkg.level.toLowerCase() === 'plus';

            return (
              <motion.div
                key={pkg.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: pkg.priority * 0.1 }}
              >
                <Card 
                  className={`hover:shadow-lg transition-shadow relative ${
                    isRecommended ? 'border-2 border-primary' : ''
                  }`}
                >
                  {isRecommended && (
                    <Chip 
                      size="sm" 
                      color="primary" 
                      className="absolute top-4 right-4 z-10"
                    >
                      推荐
                    </Chip>
                  )}

                  <CardBody className="p-6">
                    <div className="text-center">
                      <div className={`w-16 h-16 rounded-full flex items-center justify-center mx-auto mb-4 ${levelStyle.bgColor}`}>
                        <IconComponent size={24} className={`text-${levelStyle.color}`} />
                      </div>

                      <h3 className="text-lg font-bold text-default-900 mb-2">
                        {pkg.package_name}
                      </h3>

                      <div className="mb-2">
                        <Chip size="sm" color={levelStyle.color} variant="flat">
                          {pkg.level}
                        </Chip>
                      </div>

                      <div className="text-3xl font-bold text-primary mb-1">
                        ¥{pkg.price}
                      </div>
                      <div className="text-sm text-default-500 mb-6">
                        /{pkg.duration}天
                      </div>

                      {pkg.remarks && (
                        <div className="text-sm text-default-600 mb-6 min-h-[2.5rem] flex items-center justify-center">
                          {pkg.remarks}
                        </div>
                      )}

                      <Button 
                        fullWidth 
                        color={isRecommended ? "primary" : "default"}
                        variant={isRecommended ? "solid" : "bordered"}
                        isLoading={orderLoading && selectedPackage?.id === pkg.id}
                        onPress={() => {
                          setSelectedPackage(pkg);
                          createOrder(pkg.id);
                        }}
                        disabled={pkg.status !== 1}
                      >
                        {pkg.status === 1 ? '选择套餐' : '暂不可用'}
                      </Button>
                    </div>
                  </CardBody>
                </Card>
              </motion.div>
            );
          })}
        </div>
      )}

      {/* 空状态 */}
      {!loading && !error && packages.length === 0 && (
        <div className="text-center py-12 text-default-400">
          <Package size={48} className="mx-auto mb-4 opacity-50" />
          <p>暂无可用套餐</p>
        </div>
      )}

      {/* 支付弹窗 */}
      <Modal 
        isOpen={paymentModal} 
        onClose={closePaymentModal}
        size="lg"
        closeButton={paymentStatus !== 'success'}
      >
        <ModalContent>
          <ModalHeader className="flex flex-col gap-1">
            <h2 className="text-xl font-bold">支付订单</h2>
            {selectedPackage && (
              <p className="text-sm text-default-500">{selectedPackage.package_name}</p>
            )}
          </ModalHeader>

          <ModalBody>
            {orderInfo && (
              <div className="space-y-6">
                {/* 订单信息 */}
                <div className="bg-default-50 p-4 rounded-lg">
                  <div className="grid grid-cols-2 gap-4 text-sm">
                    <div>
                      <span className="text-default-500">订单号：</span>
                      <span className="font-medium">{orderInfo.order_id}</span>
                    </div>
                    <div>
                      <span className="text-default-500">支付方式：</span>
                      <span className="font-medium">{orderInfo.pay_type}</span>
                    </div>
                    <div>
                      <span className="text-default-500">金额：</span>
                      <span className="font-medium text-primary">¥{selectedPackage?.price}</span>
                    </div>
                    <div>
                      <span className="text-default-500">渠道：</span>
                      <span className="font-medium">{orderInfo.channel}</span>
                    </div>
                  </div>
                </div>

                {/* 支付状态 */}
                <div className="text-center">
                  {paymentStatus === 'pending' && (
                    <div className="space-y-3">
                      <Clock size={32} className="mx-auto text-warning" />
                      <p className="text-default-600">请使用微信扫描下方二维码支付</p>
                    </div>
                  )}

                  {paymentStatus === 'checking' && (
                    <div className="space-y-3">
                      <Spinner size="lg" color="primary" />
                      <p className="text-default-600">正在检查支付状态...</p>
                    </div>
                  )}

                  {paymentStatus === 'success' && (
                    <div className="space-y-3">
                      <CheckCircle size={32} className="mx-auto text-success" />
                      <p className="text-success font-medium">支付成功！</p>
                      <p className="text-default-500 text-sm">页面将自动关闭</p>
                    </div>
                  )}

                  {paymentStatus === 'failed' && (
                    <div className="space-y-3">
                      <AlertCircle size={32} className="mx-auto text-danger" />
                      <p className="text-danger font-medium">支付失败</p>
                      <p className="text-default-500 text-sm">请重试或联系客服</p>
                    </div>
                  )}
                </div>

                {/* 二维码 */}
                {orderInfo.qr_code && paymentStatus === 'pending' && (
                  <div className="flex justify-center">
                    <div className="bg-white p-4 rounded-lg border">
                      <img 
                        src={generateQRCode(orderInfo.qr_code)} 
                        alt="支付二维码"
                        className="w-48 h-48"
                      />
                    </div>
                  </div>
                )}
              </div>
            )}
          </ModalBody>

          <ModalFooter>
            {paymentStatus !== 'success' && (
              <Button color="danger" variant="light" onPress={closePaymentModal}>
                取消支付
              </Button>
            )}
          </ModalFooter>
        </ModalContent>
      </Modal>
    </motion.div>
  );
};
