import React, { useState, useEffect } from 'react';
import { 
  Card, 
  Button,
  Input,
  Textarea,
  Select,
  SelectItem,
  Switch,
  Divider
} from '@heroui/react';
import { Save, RefreshCw, Settings } from 'lucide-react';
import { configApi } from '../../services/api';
import { motion } from 'framer-motion';

interface ConfigItem {
  id: number;
  key: string;
  value: string;
  description?: string;
  type?: string;
  category?: string;
}

const AdminConfig: React.FC = () => {
  const [loading, setLoading] = useState(false);
  const [saveLoading, setSaveLoading] = useState(false);
  const [configs, setConfigs] = useState<ConfigItem[]>([]);

  // 配置表单数据
  const [configData, setConfigData] = useState({
    site_name: '',
    site_description: '',
    site_logo: '',
    site_favicon: '',
    maintenance_mode: false,
    debug_mode: false,
    max_upload_size: '',
    session_timeout: '',
    smtp_host: '',
    smtp_port: '',
    smtp_user: '',
    smtp_password: '',
    smtp_from: '',
    payment_enabled: false,
    wechat_pay_enabled: false,
    alipay_enabled: false,
    payment_timeout: '',
    default_language: 'zh-CN',
    timezone: 'Asia/Shanghai',
    page_size: '',
    cache_enabled: false
  });

  // 加载配置数据
  const loadConfigs = async () => {
    setLoading(true);
    try {
      const result = await configApi.list();
      const configArray = Array.isArray(result.data) ? result.data : [];
      setConfigs(configArray);

      // 设置表单初始值
      const newConfigData = { ...configData };
      configArray.forEach((config) => {
        if (config.key in newConfigData) {
          if (config.key.includes('_enabled') || config.key.includes('_mode')) {
            (newConfigData as any)[config.key] = config.value === '1' || config.value === 'true';
          } else {
            (newConfigData as any)[config.key] = config.value;
          }
        }
      });
      setConfigData(newConfigData);
    } catch (error: any) {
      console.error('加载配置失败:', error.message);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadConfigs();
  }, []);

  // 保存配置
  const handleSave = async () => {
    setSaveLoading(true);
    try {
      // 逐个更新配置
      const promises = Object.entries(configData).map(([key, value]) => {
        const stringValue = typeof value === 'boolean' ? (value ? '1' : '0') : String(value);
        return configApi.update({ key, value: stringValue });
      });

      await Promise.all(promises);
      alert('配置保存成功');
    } catch (error: any) {
      alert(error.message || '保存失败');
    } finally {
      setSaveLoading(false);
    }
  };

  // 重置表单
  const handleReset = () => {
    loadConfigs();
    alert('表单已重置');
  };

  const updateConfig = (key: string, value: any) => {
    setConfigData({ ...configData, [key]: value });
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.3 }}
      className="p-6"
    >
      <Card className="w-full">
        {/* 页面标题 */}
        <div className="flex items-center justify-between mb-6">
          <div>
            <h2 className="text-2xl font-bold flex items-center gap-2">
              <Settings className="w-6 h-6" />
              系统配置
            </h2>
            <p className="text-gray-500 mt-1">管理系统的各项配置参数</p>
          </div>
          <div className="flex gap-2">
            <Button
              variant="flat"
              startContent={<RefreshCw className="w-4 h-4" />}
              onClick={loadConfigs}
              isLoading={loading}
            >
              刷新
            </Button>
            <Button onClick={handleReset}>
              重置
            </Button>
            <Button
              color="primary"
              startContent={<Save className="w-4 h-4" />}
              onClick={handleSave}
              isLoading={saveLoading}
            >
              保存配置
            </Button>
          </div>
        </div>

        <div className="space-y-8">
          {/* 基础设置 */}
          <Card className="p-6">
            <h3 className="text-lg font-semibold mb-4">基础设置</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <Input
                label="网站名称"
                placeholder="请输入网站名称"
                value={configData.site_name}
                onChange={(e) => updateConfig('site_name', e.target.value)}
              />
              <Input
                label="网站Logo"
                placeholder="请输入Logo URL"
                value={configData.site_logo}
                onChange={(e) => updateConfig('site_logo', e.target.value)}
              />
              <Input
                label="网站图标"
                placeholder="请输入图标URL"
                value={configData.site_favicon}
                onChange={(e) => updateConfig('site_favicon', e.target.value)}
              />
            </div>
            <div className="mt-4">
              <Textarea
                label="网站描述"
                placeholder="请输入网站描述"
                value={configData.site_description}
                onChange={(e) => updateConfig('site_description', e.target.value)}
                maxRows={3}
              />
            </div>
          </Card>

          {/* 系统设置 */}
          <Card className="p-6">
            <h3 className="text-lg font-semibold mb-4">系统设置</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div className="flex items-center justify-between">
                <div>
                  <label className="text-sm font-medium">维护模式</label>
                  <p className="text-xs text-gray-500">是否开启维护模式</p>
                </div>
                <Switch
                  isSelected={configData.maintenance_mode}
                  onValueChange={(value) => updateConfig('maintenance_mode', value)}
                />
              </div>

              <div className="flex items-center justify-between">
                <div>
                  <label className="text-sm font-medium">调试模式</label>
                  <p className="text-xs text-gray-500">是否开启调试模式</p>
                </div>
                <Switch
                  isSelected={configData.debug_mode}
                  onValueChange={(value) => updateConfig('debug_mode', value)}
                />
              </div>

              <Input
                label="上传限制(MB)"
                placeholder="请输入上传文件大小限制"
                type="number"
                value={configData.max_upload_size}
                onChange={(e) => updateConfig('max_upload_size', e.target.value)}
              />

              <Input
                label="会话超时(分钟)"
                placeholder="请输入会话超时时间"
                type="number"
                value={configData.session_timeout}
                onChange={(e) => updateConfig('session_timeout', e.target.value)}
              />
            </div>
          </Card>

          {/* 邮件设置 */}
          <Card className="p-6">
            <h3 className="text-lg font-semibold mb-4">邮件设置</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <Input
                label="SMTP服务器"
                placeholder="请输入SMTP服务器地址"
                value={configData.smtp_host}
                onChange={(e) => updateConfig('smtp_host', e.target.value)}
              />
              <Input
                label="SMTP端口"
                placeholder="请输入SMTP端口"
                type="number"
                value={configData.smtp_port}
                onChange={(e) => updateConfig('smtp_port', e.target.value)}
              />
              <Input
                label="SMTP用户名"
                placeholder="请输入SMTP用户名"
                value={configData.smtp_user}
                onChange={(e) => updateConfig('smtp_user', e.target.value)}
              />
              <Input
                label="SMTP密码"
                placeholder="请输入SMTP密码"
                type="password"
                value={configData.smtp_password}
                onChange={(e) => updateConfig('smtp_password', e.target.value)}
              />
              <Input
                label="发件人邮箱"
                placeholder="请输入发件人邮箱"
                value={configData.smtp_from}
                onChange={(e) => updateConfig('smtp_from', e.target.value)}
                className="md:col-span-2"
              />
            </div>
          </Card>

          {/* 支付设置 */}
          <Card className="p-6">
            <h3 className="text-lg font-semibold mb-4">支付设置</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
              <div className="flex items-center justify-between">
                <div>
                  <label className="text-sm font-medium">支付功能</label>
                  <p className="text-xs text-gray-500">是否启用支付功能</p>
                </div>
                <Switch
                  isSelected={configData.payment_enabled}
                  onValueChange={(value) => updateConfig('payment_enabled', value)}
                />
              </div>

              <div className="flex items-center justify-between">
                <div>
                  <label className="text-sm font-medium">微信支付</label>
                  <p className="text-xs text-gray-500">是否启用微信支付</p>
                </div>
                <Switch
                  isSelected={configData.wechat_pay_enabled}
                  onValueChange={(value) => updateConfig('wechat_pay_enabled', value)}
                />
              </div>

              <div className="flex items-center justify-between">
                <div>
                  <label className="text-sm font-medium">支付宝支付</label>
                  <p className="text-xs text-gray-500">是否启用支付宝支付</p>
                </div>
                <Switch
                  isSelected={configData.alipay_enabled}
                  onValueChange={(value) => updateConfig('alipay_enabled', value)}
                />
              </div>

              <Input
                label="支付超时(分钟)"
                placeholder="请输入支付超时时间"
                type="number"
                value={configData.payment_timeout}
                onChange={(e) => updateConfig('payment_timeout', e.target.value)}
              />
            </div>
          </Card>

          {/* 其他设置 */}
          <Card className="p-6">
            <h3 className="text-lg font-semibold mb-4">其他设置</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <Select
                label="默认语言"
                placeholder="请选择默认语言"
                selectedKeys={[configData.default_language]}
                onSelectionChange={(keys) => {
                  const value = Array.from(keys)[0] as string;
                  updateConfig('default_language', value);
                }}
              >
                <SelectItem key="zh-CN">中文</SelectItem>
                <SelectItem key="en-US">English</SelectItem>
              </Select>

              <Select
                label="时区"
                placeholder="请选择时区"
                selectedKeys={[configData.timezone]}
                onSelectionChange={(keys) => {
                  const value = Array.from(keys)[0] as string;
                  updateConfig('timezone', value);
                }}
              >
                <SelectItem key="Asia/Shanghai">Asia/Shanghai</SelectItem>
                <SelectItem key="UTC">UTC</SelectItem>
              </Select>

              <Input
                label="默认分页大小"
                placeholder="请输入默认分页大小"
                type="number"
                value={configData.page_size}
                onChange={(e) => updateConfig('page_size', e.target.value)}
              />

              <div className="flex items-center justify-between">
                <div>
                  <label className="text-sm font-medium">缓存功能</label>
                  <p className="text-xs text-gray-500">是否启用缓存</p>
                </div>
                <Switch
                  isSelected={configData.cache_enabled}
                  onValueChange={(value) => updateConfig('cache_enabled', value)}
                />
              </div>
            </div>
          </Card>
        </div>

        <Divider className="my-8" />

        {/* 底部操作按钮 */}
        <div className="flex justify-center gap-4">
          <Button size="lg" onClick={handleReset}>
            重置表单
          </Button>
          <Button
            color="primary"
            size="lg"
            startContent={<Save className="w-4 h-4" />}
            onClick={handleSave}
            isLoading={saveLoading}
          >
            保存所有配置
          </Button>
        </div>
      </Card>
    </motion.div>
  );
};

export default AdminConfig;
