 */

// Ëé∑ÂèñÂΩìÂâçÂüüÂêç‰ø°ÊÅØ
const getCurrentDomain = (): { currentDomain: string; mainDomain: string } => {
  const hostname = window.location.hostname;
  const parts = hostname.split('.');

  // Â¶ÇÊûúÊòØIPÂú∞ÂùÄÊàñlocalhostÔºåÁõ¥Êé•ËøîÂõûÂΩìÂâçÂüüÂêç
  if (hostname === 'localhost' || /^\d+\.\d+\.\d+\.\d+$/.test(hostname)) {
    return {
      currentDomain: hostname,
      mainDomain: hostname
    };
  }

  // Ëé∑Âèñ‰∏ªÂüüÂêçÔºàÊúÄÂêé‰∏§‰∏™ÈÉ®ÂàÜÔºåÂ¶Ç yy.comÔºâ
  const mainDomain = parts.length >= 2 ? parts.slice(-2).join('.') : hostname;

  return {
    currentDomain: hostname,
    mainDomain: mainDomain
  };
};

// Âà†Èô§ÊåáÂÆöÂüüÂêç‰∏ãÁöÑÊâÄÊúâcookie
const clearCookiesForDomain = (domain: string): void => {
  const cookies = document.cookie.split(';');

  for (let cookie of cookies) {
    const eqPos = cookie.indexOf('=');
    const name = eqPos > -1 ? cookie.substr(0, eqPos).trim() : cookie.trim();

    if (name) {
      // Â∞ùËØïÂ§öÁßçÂà†Èô§ÊñπÂºèÔºåÁ°Æ‰øùÂΩªÂ∫ïÊ∏ÖÈô§
      const expireDate = 'Thu, 01 Jan 1970 00:00:00 GMT';

      // 1. Âà†Èô§ÂΩìÂâçÂüüÂêçÔºåÊ†πË∑ØÂæÑ
      document.cookie = `${name}=; expires=${expireDate}; path=/`;

      // 2. Âà†Èô§ÂΩìÂâçÂüüÂêçÔºåÂΩìÂâçË∑ØÂæÑ
      document.cookie = `${name}=; expires=${expireDate}; path=${window.location.pathname}`;

      // 3. Âà†Èô§ÊåáÂÆöÂüüÂêçÔºåÊ†πË∑ØÂæÑ
      document.cookie = `${name}=; expires=${expireDate}; path=/; domain=${domain}`;

      // 4. Âà†Èô§ÊåáÂÆöÂüüÂêçÂâçÁºÄÁÇπÔºåÊ†πË∑ØÂæÑ
      document.cookie = `${name}=; expires=${expireDate}; path=/; domain=.${domain}`;

      // 5. Âà†Èô§ÂΩìÂâçÂÆåÊï¥ÂüüÂêç
      document.cookie = `${name}=; expires=${expireDate}; path=/; domain=${window.location.hostname}`;

      // 6. Âà†Èô§ÂΩìÂâçÂÆåÊï¥ÂüüÂêçÂâçÁºÄÁÇπ
      document.cookie = `${name}=; expires=${expireDate}; path=/; domain=.${window.location.hostname}`;

      // 7. Â∞ùËØïÂà†Èô§ÊâÄÊúâÂèØËÉΩÁöÑË∑ØÂæÑÁªÑÂêà
      const pathParts = window.location.pathname.split('/').filter(part => part);
      let currentPath = '';
      for (const part of pathParts) {
        currentPath += `/${part}`;
        document.cookie = `${name}=; expires=${expireDate}; path=${currentPath}`;
        document.cookie = `${name}=; expires=${expireDate}; path=${currentPath}; domain=${domain}`;
        document.cookie = `${name}=; expires=${expireDate}; path=${currentPath}; domain=.${domain}`;
      }
    }
  }
};

// ÂΩªÂ∫ïÊ∏ÖÈô§ÊâÄÊúâÁõ∏ÂÖ≥ÂüüÂêçÁöÑcookieÔºàÁÆÄÂåñÂèØÈù†ÁâàÔºâ
const clearAllRelatedCookies = (currentDomain: string, mainDomain: string): void => {
  try {
    // Ëé∑ÂèñÊâÄÊúâÂèØËÉΩÁöÑÂüüÂêçÂèò‰Ωì
    const domainsToClean = [
      currentDomain,
      mainDomain,
      window.location.hostname,
      `.${currentDomain}`,
      `.${mainDomain}`,
      `.${window.location.hostname}`
    ];

    // ÂéªÈáç
    const uniqueDomains = Array.from(new Set(domainsToClean));

    // Ëé∑ÂèñÂΩìÂâçÊâÄÊúâcookieÂêçÁß∞
    const cookies = document.cookie.split(';');
    const cookieNames = cookies.map(cookie => {
      const name = cookie.split('=')[0].trim();
      return name;
    }).filter(name => name);

    // ÂØπÊØè‰∏™cookieÂêçÁß∞ÔºåÂ∞ùËØïÁî®ÊâÄÊúâÂèØËÉΩÁöÑÂüüÂêçÂíåË∑ØÂæÑÁªÑÂêàËøõË°åÂà†Èô§
    cookieNames.forEach(cookieName => {
      if (cookieName) {
        // Âü∫Á°ÄÂà†Èô§ÊñπÂºè
        document.cookie = `${cookieName}=; expires=Thu, 01 Jan 1970 00:00:00 GMT; path=/`;

        // ÈíàÂØπÊØè‰∏™ÂüüÂêçËøõË°åÂà†Èô§
        uniqueDomains.forEach(domain => {
          try {
            document.cookie = `${cookieName}=; expires=Thu, 01 Jan 1970 00:00:00 GMT; path=/; domain=${domain}`;
            document.cookie = `${cookieName}=; expires=Thu, 01 Jan 1970 00:00:00 GMT; path=/; domain=${domain}; secure`;
          } catch (e) {
            // ÂøΩÁï•Âçï‰∏™Âà†Èô§Êìç‰ΩúÁöÑÈîôËØØ
          }
        });
      }
    });

    // È¢ùÂ§ñ‰øùÈô©ÔºöÂÜçÊ¨°Â∞ùËØïÂà†Èô§Â∏∏ËßÅcookie
    const commonCookieNames = [
      'xuserid', 'xtoken', 'cas_access_token', 'access_token', 'refresh_token',
      'user_session', 'auth_token', 'session_id', 'JSESSIONID'
    ];

    commonCookieNames.forEach(cookieName => {
      document.cookie = `${cookieName}=; expires=Thu, 01 Jan 1970 00:00:00 GMT; path=/`;
      document.cookie = `${cookieName}=; expires=Thu, 01 Jan 1970 00:00:00 GMT; path=/; domain=.niceaigc.com`;
      document.cookie = `${cookieName}=; expires=Thu, 01 Jan 1970 00:00:00 GMT; path=/; domain=niceaigc.com`;
    });

  } catch (error) {
    // Â¶ÇÊûúÊâπÈáèÊ∏ÖÁêÜÂ§±Ë¥•ÔºåËøõË°åÂü∫Á°ÄÊ∏ÖÁêÜ
    try {
      const allCookies = document.cookie.split(';');
      allCookies.forEach(cookie => {
        const cookieName = cookie.split('=')[0].trim();
        if (cookieName) {
          document.cookie = `${cookieName}=; expires=Thu, 01 Jan 1970 00:00:00 GMT; path=/`;
        }
      });
    } catch (basicError) {
      // ËøûÂü∫Á°ÄÊ∏ÖÁêÜÈÉΩÂ§±Ë¥•‰∫ÜÔºåÂè™ËÉΩÂøΩÁï•
    }
  }
};

// ÈÄÄÂá∫ÁôªÂΩïÂäüËÉΩÔºàÂº∫ÂåñÁâàÔºâ
export const logout = async (): Promise<void> => {
  console.log('üöÄ ÂºÄÂßãÊâßË°ålogoutÊµÅÁ®ã');

  try {
    const { currentDomain, mainDomain } = getCurrentDomain();
    console.log('üìç ÂüüÂêç‰ø°ÊÅØ:', { currentDomain, mainDomain });

    // Âú®Ê∏ÖÈô§cookie‰πãÂâçÂÖàËé∑Âèñcas_access_token
    const casAccessToken = getCookie('cas_access_token');
    console.log('üîë Ëé∑ÂèñÂà∞ÁöÑcas_access_token:', casAccessToken ? 'Â≠òÂú®' : '‰∏çÂ≠òÂú®');

    try {
      // Âä®ÊÄÅÂØºÂÖ• postMessage Â∑•ÂÖ∑ÔºàÈÅøÂÖçÂæ™ÁéØ‰æùËµñÔºâ
      console.log('üìû ÂáÜÂ§áÂèëÈÄÅpostMessageÈÄöÁü•');
      const { notifyLogout } = await import('./postMessage');

      // ÂÖàÂèëÈÄÅÈÄÄÂá∫ÁôªÂΩïÈÄöÁü•Âà∞Áà∂È°µÈù¢ÔºåËá™Âä®Ëé∑ÂèñÂΩìÂâçÈ°µÈù¢Âú∞ÂùÄ
      notifyLogout();
      console.log('‚úÖ postMessageÈÄöÁü•ÂèëÈÄÅÂÆåÊàê');

      // Á≠âÂæÖ‰∏ÄÂ∞èÊÆµÊó∂Èó¥Á°Æ‰øùÊ∂àÊÅØÂèëÈÄÅÂÆåÊàê
      await new Promise(resolve => setTimeout(resolve, 100));
    } catch (postMessageError) {
      console.warn('‚ö†Ô∏è postMessageÂèëÈÄÅÂ§±Ë¥•ÔºåÁªßÁª≠ÊâßË°åÊ∏ÖÁêÜ:', postMessageError);
      // ‰∏çËÆ©postMessageÈîôËØØ‰∏≠Êñ≠Êï¥‰∏™ÊµÅÁ®ã
    }

    // Ê∏ÖÈô§localStorageÂíåsessionStorage
    console.log('üóëÔ∏è ÂºÄÂßãÊ∏ÖÈô§Êú¨Âú∞Â≠òÂÇ®');
    try {
      localStorage.clear();
      sessionStorage.clear();
      console.log('‚úÖ Êú¨Âú∞Â≠òÂÇ®Ê∏ÖÈô§ÂÆåÊàê');
    } catch (storageError) {
      console.error('‚ùå Êú¨Âú∞Â≠òÂÇ®Ê∏ÖÈô§Â§±Ë¥•:', storageError);
    }

    // ÂΩªÂ∫ïÊ∏ÖÈô§ÊâÄÊúâÁõ∏ÂÖ≥ÂüüÂêçÁöÑcookie
    console.log('üç™ ÂºÄÂßãÊ∏ÖÈô§cookies');
    try {
      console.log('üç™ Ê∏ÖÈô§ÂâçÁöÑcookies:', document.cookie);
      clearAllRelatedCookies(currentDomain, mainDomain);
      console.log('üç™ Ê∏ÖÈô§ÂêéÁöÑcookies:', document.cookie);
      console.log('‚úÖ CookieÊ∏ÖÈô§ÂÆåÊàê');
    } catch (cookieError) {
      console.error('‚ùå CookieÊ∏ÖÈô§Â§±Ë¥•:', cookieError);
    }

    // Á°ÆÂÆöÊúÄÁªàË∑≥ËΩ¨Âú∞ÂùÄ
    let redirectUri: string;
    if (currentDomain !== mainDomain) {
      // ÂΩìÂâçÊòØÂ≠êÂüüÂêçÔºåË∑≥ËΩ¨Âà∞‰∏ªÂüüÂêç
      redirectUri = `${window.location.protocol}//${mainDomain}`;
    } else {
      // ÂΩìÂâçÊòØ‰∏ªÂüüÂêçÔºåË∑≥ËΩ¨Âà∞Ê†πË∑ØÂæÑ
      redirectUri = `${window.location.protocol}//${currentDomain}/`;
    }
    console.log('üîó ÈáçÂÆöÂêëURI:', redirectUri);

    // ÊûÑÈÄ†Casdoor logout URL
    let casdoorLogoutUrl = `https://casdoor.niceaigc.com/api/logout?post_logout_redirect_uri=${encodeURIComponent(redirectUri)}`;

    // Â¶ÇÊûúÊúâcas_access_tokenÔºåÊ∑ªÂä†id_token_hintÂèÇÊï∞
    if (casAccessToken) {
      casdoorLogoutUrl += `&id_token_hint=${encodeURIComponent(casAccessToken)}`;
    }

    console.log('üéØ ÊúÄÁªàË∑≥ËΩ¨URL:', casdoorLogoutUrl);

    // Âº∫Âà∂Á≠âÂæÖ‰∏Ä‰∏ãÔºåÁ°Æ‰øùÊâÄÊúâÊìç‰ΩúÂÆåÊàê
    await new Promise(resolve => setTimeout(resolve, 200));

    console.log('üöÄ ÂáÜÂ§áË∑≥ËΩ¨Âà∞Casdoor');
    // Ë∑≥ËΩ¨Âà∞Casdoor logoutÊé•Âè£
    window.location.href = casdoorLogoutUrl;

  } catch (error) {
    console.error('‚ùå logoutÂáΩÊï∞ÊâßË°åÂ§±Ë¥•:', error);

    // Â¶ÇÊûúÂÖ®ÊµÅÁ®ãÂ§±Ë¥•ÔºåËá≥Â∞ëÊâßË°åÂü∫Á°ÄÊ∏ÖÁêÜ
    console.log('üõ°Ô∏è ÊâßË°åÂ∫îÊÄ•Ê∏ÖÁêÜ');
    try {
      localStorage.clear();
      sessionStorage.clear();

      // Âü∫Á°ÄcookieÊ∏ÖÁêÜ
      const cookies = document.cookie.split(';');
      cookies.forEach(cookie => {
        const cookieName = cookie.split('=')[0].trim();
        if (cookieName) {
          document.cookie = `${cookieName}=; expires=Thu, 01 Jan 1970 00:00:00 GMT; path=/`;
          document.cookie = `${cookieName}=; expires=Thu, 01 Jan 1970 00:00:00 GMT; path=/; domain=.niceaigc.com`;
        }
      });

      console.log('üõ°Ô∏è Â∫îÊÄ•Ê∏ÖÁêÜÂÆåÊàêÔºåÂº∫Âà∂Ë∑≥ËΩ¨Âà∞‰∏ªÈ°µ');
      // Â∫îÊÄ•Ë∑≥ËΩ¨
      window.location.href = 'https://niceaigc.com/';
    } catch (emergencyError) {
      console.error('‚ùå Â∫îÊÄ•Ê∏ÖÁêÜ‰πüÂ§±Ë¥•‰∫Ü:', emergencyError);
      // ÊúÄÂêéÁöÑÊâãÊÆµ
      alert('ÈÄÄÂá∫ÁôªÂΩïÊó∂ÂèëÁîüÈîôËØØÔºåËØ∑ÊâãÂä®Ê∏ÖÈô§ÊµèËßàÂô®ÁºìÂ≠òÊàñÂà∑Êñ∞È°µÈù¢');
    }
  }
};

// Ëé∑ÂèñcookieÂÄº
export const getCookie = (name: string): string | null => {
  const value = `; ${document.cookie}`;
  const parts = value.split(`; ${name}=`);
  if (parts.length === 2) {
    return parts.pop()?.split(';').shift() || null;
  }
  return null;
};

// Ê£ÄÊü•ÊòØÂê¶Â∑≤ÁôªÂΩï
export const isAuthenticated = (): boolean => {
  const xuserid = getCookie('xuserid');
  const xtoken = getCookie('xtoken');
  return !!(xuserid && xtoken);
};
