import React, { useState, useEffect } from 'react';
import { 
  Card, 
  Button, 
  Modal, 
  ModalContent, 
  ModalHeader, 
  ModalBody, 
  ModalFooter,
  Input,
  Select,
  SelectItem,
  Table,
  TableHeader,
  TableColumn,
  TableBody,
  TableRow,
  TableCell,
  Chip,
  Avatar,
  useDisclosure
} from '@heroui/react';
import { Plus, Edit, Trash2, RefreshCw, User } from 'lucide-react';
import { userApi } from '../../services/api';
import { motion } from 'framer-motion';

interface User {
  id: number;
  username: string;
  email?: string;
  phone?: string;
  status?: number;
  level?: number;
  created_at?: string;
  updated_at?: string;
  last_login?: string;
}

const AdminUsers: React.FC = () => {
  const [data, setData] = useState<User[]>([]);
  const [loading, setLoading] = useState(false);
  const [editingRecord, setEditingRecord] = useState<User | null>(null);
  const { isOpen, onOpen, onOpenChange } = useDisclosure();

  // 表单数据
  const [formData, setFormData] = useState({
    username: '',
    email: '',
    phone: '',
    level: '1',
    status: '1',
    password: ''
  });

  // 加载数据
  const loadData = async () => {
    setLoading(true);
    try {
      const result = await userApi.list({
        page: 1,
        limit: 50,
      });

      setData(Array.isArray(result.data) ? result.data : []);
    } catch (error: any) {
      console.error('加载数据失败:', error.message);
      setData([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadData();
  }, []);

  // 新增用户
  const handleAdd = () => {
    setEditingRecord(null);
    setFormData({
      username: '',
      email: '',
      phone: '',
      level: '1',
      status: '1',
      password: ''
    });
    onOpen();
  };

  // 编辑用户
  const handleEdit = (record: User) => {
    setEditingRecord(record);
    setFormData({
      username: record.username,
      email: record.email || '',
      phone: record.phone || '',
      level: String(record.level || 1),
      status: String(record.status || 1),
      password: ''
    });
    onOpen();
  };

  // 删除用户
  const handleDelete = async (id: number) => {
    if (!window.confirm('确定删除这个用户吗？')) return;

    try {
      await userApi.delete(id);
      alert('删除成功');
      loadData();
    } catch (error: any) {
      alert(error.message || '删除失败');
    }
  };

  // 保存用户
  const handleSave = async () => {
    try {
      if (editingRecord) {
        await userApi.update({ ...editingRecord, ...formData });
        alert('更新成功');
      } else {
        await userApi.create(formData);
        alert('创建成功');
      }

      onOpenChange();
      loadData();
    } catch (error: any) {
      alert(error.message || '保存失败');
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.3 }}
      className="p-6"
    >
      <Card className="w-full">
        {/* 页面标题 */}
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-2xl font-bold">用户管理</h2>
          <div className="flex gap-2">
            <Button
              variant="flat"
              startContent={<RefreshCw className="w-4 h-4" />}
              onClick={loadData}
              isLoading={loading}
            >
              刷新
            </Button>
            <Button
              color="primary"
              startContent={<Plus className="w-4 h-4" />}
              onClick={handleAdd}
            >
              新增用户
            </Button>
          </div>
        </div>

        {/* 简化的数据展示 */}
        <div className="space-y-4">
          {loading ? (
            <div className="text-center py-8">加载中...</div>
          ) : data.length === 0 ? (
            <div className="text-center py-8 text-gray-500">暂无数据</div>
          ) : (
            <div className="grid gap-4">
              {data.map((user) => (
                <Card key={user.id} className="p-4">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-4">
                      <Avatar 
                        icon={<User className="w-4 h-4" />}
                        size="sm"
                      />
                      <div>
                        <div className="font-medium">{user.username}</div>
                        {user.email && (
                          <div className="text-sm text-gray-500">{user.email}</div>
                        )}
                        {user.phone && (
                          <div className="text-sm text-gray-500">{user.phone}</div>
                        )}
                      </div>
                    </div>
                    <div className="flex items-center gap-4">
                      <Chip 
                        color="primary" 
                        variant="flat"
                        size="sm"
                      >
                        L{user.level || 1}
                      </Chip>
                      <Chip 
                        color={user.status === 1 ? 'success' : 'danger'} 
                        variant="flat"
                        size="sm"
                      >
                        {user.status === 1 ? '正常' : '禁用'}
                      </Chip>
                      <div className="flex gap-2">
                        <Button
                          size="sm"
                          variant="flat"
                          isIconOnly
                          onClick={() => handleEdit(user)}
                        >
                          <Edit className="w-4 h-4" />
                        </Button>
                        <Button
                          size="sm"
                          variant="flat"
                          color="danger"
                          isIconOnly
                          onClick={() => handleDelete(user.id)}
                        >
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      </div>
                    </div>
                  </div>
                </Card>
              ))}
            </div>
          )}
        </div>
      </Card>

      {/* 编辑/新增模态框 */}
      <Modal isOpen={isOpen} onOpenChange={onOpenChange} size="2xl">
        <ModalContent>
          {(onClose) => (
            <>
              <ModalHeader>
                {editingRecord ? '编辑用户' : '新增用户'}
              </ModalHeader>
              <ModalBody>
                <div className="space-y-4">
                  <Input
                    label="用户名"
                    placeholder="请输入用户名"
                    value={formData.username}
                    onChange={(e) => setFormData({...formData, username: e.target.value})}
                    isRequired
                  />

                  <Input
                    label="邮箱"
                    placeholder="请输入邮箱地址"
                    type="email"
                    value={formData.email}
                    onChange={(e) => setFormData({...formData, email: e.target.value})}
                  />

                  <Input
                    label="手机号"
                    placeholder="请输入手机号"
                    value={formData.phone}
                    onChange={(e) => setFormData({...formData, phone: e.target.value})}
                  />

                  <div className="grid grid-cols-2 gap-4">
                    <Select
                      label="用户等级"
                      placeholder="请选择用户等级"
                      selectedKeys={[formData.level]}
                      onSelectionChange={(keys) => {
                        const value = Array.from(keys)[0] as string;
                        setFormData({...formData, level: value});
                      }}
                    >
                      <SelectItem key="1">普通用户</SelectItem>
                      <SelectItem key="2">VIP用户</SelectItem>
                      <SelectItem key="3">超级VIP</SelectItem>
                    </Select>

                    <Select
                      label="状态"
                      placeholder="请选择状态"
                      selectedKeys={[formData.status]}
                      onSelectionChange={(keys) => {
                        const value = Array.from(keys)[0] as string;
                        setFormData({...formData, status: value});
                      }}
                    >
                      <SelectItem key="1">正常</SelectItem>
                      <SelectItem key="0">禁用</SelectItem>
                    </Select>
                  </div>

                  {!editingRecord && (
                    <Input
                      label="密码"
                      placeholder="请输入密码"
                      type="password"
                      value={formData.password}
                      onChange={(e) => setFormData({...formData, password: e.target.value})}
                      isRequired
                    />
                  )}
                </div>
              </ModalBody>
              <ModalFooter>
                <Button variant="flat" onPress={onClose}>
                  取消
                </Button>
                <Button color="primary" onPress={() => { handleSave(); onClose(); }}>
                  保存
                </Button>
              </ModalFooter>
            </>
          )}
        </ModalContent>
      </Modal>
    </motion.div>
  );
};

export default AdminUsers;
