/**
 * 订阅套餐Tab页面
 * 显示用户当前订阅和可用套餐
 */
import React, { useState, useEffect } from 'react';
import { Card, CardBody, CardHeader, Button, Chip, Spinner, Modal, ModalContent, ModalHeader, ModalBody, ModalFooter, Divider } from '@heroui/react';
import { motion } from 'framer-motion';
import { Package, Star, Crown, AlertCircle, CheckCircle, Clock, QrCode } from 'lucide-react';
import { packageUserApi, orderUserApi } from '../../../services/userApi';
import QRCodeGenerator from 'qrcode-generator';

interface PackageInfo {
  id: number;
  package_name: string;
  category: string;
  price: number;
  duration: number;
  introduce: string;
  level: string;
  priority: number;
  remarks: string;
  status: number;
}

interface OrderInfo {
  success: boolean;
  trade_no: string;
  order_id: string;
  payment_url: string | null;
  qr_code: string;
  channel: string;
  pay_type: string;
}

export const SubscriptionTab: React.FC = () => {
  const [packages, setPackages] = useState<PackageInfo[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string>('');
  const [selectedPackage, setSelectedPackage] = useState<PackageInfo | null>(null);
  const [orderInfo, setOrderInfo] = useState<OrderInfo | null>(null);
  const [orderLoading, setOrderLoading] = useState(false);
  const [paymentModal, setPaymentModal] = useState(false);
  const [paymentStatus, setPaymentStatus] = useState<'pending' | 'checking' | 'success' | 'failed'>('pending');
  const [checkInterval, setCheckInterval] = useState<NodeJS.Timeout | null>(null);

  // 获取套餐列表
  const fetchPackages = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await packageUserApi.getPackages();

      if (response.code === 20000) {
        setPackages(response.data || []);
      } else {
        setError(response.msg || '获取套餐列表失败');
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : '网络错误');
    } finally {
      setLoading(false);
    }
  };

  // 创建订单
  const createOrder = async (packageId: number) => {
    try {
      setOrderLoading(true);
      const response = await orderUserApi.createOrder(packageId);

      if (response.code === 20000) {
        setOrderInfo(response.data);
        setPaymentModal(true);
        setPaymentStatus('pending');
        startPaymentStatusCheck(response.data.order_id);
      } else {
        alert(response.msg || '创建订单失败');
      }
    } catch (err) {
      alert(err instanceof Error ? err.message : '网络错误');
    } finally {
      setOrderLoading(false);
    }
  };

  // 开始检查支付状态
  const startPaymentStatusCheck = (orderId: string) => {
    if (checkInterval) {
      clearInterval(checkInterval);
    }

    const interval = setInterval(async () => {
      try {
        setPaymentStatus('checking');
        const response = await orderUserApi.getPayStatus(orderId);

        if (response.code === 20000) {
          setPaymentStatus('success');
          clearInterval(interval);
          setTimeout(() => {
            setPaymentModal(false);
            fetchPackages(); // 刷新套餐列表
          }, 3000);
        } else if (response.code === 20001) {
          setPaymentStatus('pending');
        } else {
          setPaymentStatus('failed');
          clearInterval(interval);
        }
      } catch (err) {
        console.error('检查支付状态失败:', err);
      }
    }, 3000); // 每3秒检查一次

    setCheckInterval(interval);
  };

  // 生成二维码
  const generateQRCode = (text: string): string => {
    const qr = QRCodeGenerator(0, 'M');
    qr.addData(text);
    qr.make();
    return qr.createDataURL(8, 4);
  };

  // 获取套餐等级样式
  const getPackageLevelColor = (level: string) => {
    switch (level.toLowerCase()) {
      case 'base':
        return 'default' as const;
      case 'plus':
        return 'primary' as const;
      case 'pro':
      case 'premium':
        return 'secondary' as const;
      default:
        return 'default' as const;
    }
  };

  // 获取套餐图标
  const getPackageIcon = (level: string) => {
    switch (level.toLowerCase()) {
      case 'base':
        return Package;
      case 'plus':
        return Star;
      case 'pro':
      case 'premium':
        return Crown;
      default:
        return Package;
    }
  };

  // 关闭支付弹窗
  const closePaymentModal = () => {
    if (checkInterval) {
      clearInterval(checkInterval);
      setCheckInterval(null);
    }
    setPaymentModal(false);
    setOrderInfo(null);
    setPaymentStatus('pending');
  };

  useEffect(() => {
    fetchPackages();

    // 清理定时器
    return () => {
      if (checkInterval) {
        clearInterval(checkInterval);
      }
    };
  }, []);

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      className="space-y-6"
    >
      {/* 页面标题 */}
      <div className="flex items-center gap-3">
        <div className="w-8 h-8 bg-primary/10 rounded-lg flex items-center justify-center">
          <Package size={20} className="text-primary" />
        </div>
        <div>
          <h1 className="text-2xl font-bold text-default-900">订阅套餐</h1>
          <p className="text-sm text-default-500 mt-1">选择适合您的套餐方案</p>
        </div>
      </div>

      {/* 加载状态 */}
      {loading && (
        <div className="flex items-center justify-center py-12">
          <Spinner size="lg" color="primary" />
          <span className="ml-3 text-default-600">加载中...</span>
        </div>
      )}

      {/* 错误状态 */}
      {error && (
        <Card className="border-danger/20">
          <CardBody className="p-6">
            <div className="flex items-start gap-4">
              <AlertCircle size={20} className="text-danger flex-shrink-0 mt-1" />
              <div>
                <h3 className="font-semibold text-danger mb-2">加载失败</h3>
                <p className="text-default-600 text-sm">{error}</p>
                <Button
                  size="sm"
                  color="danger"
                  variant="light"
                  className="mt-3"
                  onPress={fetchPackages}
                >
                  重试
                </Button>
              </div>
            </div>
          </CardBody>
        </Card>
      )}

      {/* 套餐列表 */}
      {!loading && !error && packages.length > 0 && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {packages.map((pkg) => {
            const levelColor = getPackageLevelColor(pkg.level);
            const IconComponent = getPackageIcon(pkg.level);
            const isRecommended = pkg.level.toLowerCase() === 'plus';

            return (
              <motion.div
                key={pkg.id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                transition={{ delay: pkg.priority * 0.1 }}
              >
                <Card className={`relative ${isRecommended ? 'border-primary-200 shadow-lg' : ''}`}>
                  {isRecommended && (
                    <div className="absolute -top-2 left-1/2 transform -translate-x-1/2 z-10">
                      <Chip size="sm" color="primary">
                        推荐
                      </Chip>
                    </div>
                  )}

                  <CardHeader className="flex flex-col items-center pb-2 pt-6">
                    <div className={`p-3 rounded-full bg-${levelColor}/10 mb-3`}>
                      <IconComponent size={24} className={`text-${levelColor}`} />
                    </div>
                    <h3 className="text-lg font-semibold text-center">
                      {pkg.package_name}
                    </h3>
                    <Chip size="sm" color={levelColor} variant="flat" className="mt-2">
                      {pkg.level}
                    </Chip>
                  </CardHeader>

                  <Divider />

                  <CardBody className="pt-4">
                    <div className="text-center space-y-4">
                      <div>
                        <div className="text-3xl font-bold text-primary">
                          ¥{pkg.price}
                        </div>
                        <div className="text-sm text-default-500">
                          {pkg.duration}天有效期
                        </div>
                      </div>

                      {pkg.remarks && (
                        <p className="text-sm text-default-600 min-h-[2.5rem] flex items-center justify-center">
                          {pkg.remarks}
                        </p>
                      )}

                      <Button 
                        fullWidth 
                        color={isRecommended ? "primary" : "default"}
                        size="lg"
                        isLoading={orderLoading && selectedPackage?.id === pkg.id}
                        onPress={() => {
                          setSelectedPackage(pkg);
                          createOrder(pkg.id);
                        }}
                        isDisabled={pkg.status !== 1}
                        className="mt-4"
                      >
                        {pkg.status === 1 ? '立即购买' : '暂不可用'}
                      </Button>
                    </div>
                  </CardBody>
                </Card>
              </motion.div>
            );
          })}
        </div>
      )}

      {/* 空状态 */}
      {!loading && !error && packages.length === 0 && (
        <div className="text-center py-12 text-default-400">
          <Package size={48} className="mx-auto mb-4 opacity-50" />
          <p>暂无可用套餐</p>
        </div>
      )}

      {/* 支付弹窗 */}
      <Modal 
        isOpen={paymentModal} 
        onClose={closePaymentModal}
        size="lg"
        hideCloseButton={paymentStatus === 'success'}
      >
        <ModalContent>
          <ModalHeader>
            <div>
              <h2 className="text-xl font-bold">支付订单</h2>
              {selectedPackage && (
                <p className="text-sm text-default-500 mt-1">{selectedPackage.package_name}</p>
              )}
            </div>
          </ModalHeader>

          <ModalBody>
            {orderInfo && (
              <div className="space-y-6">
                {/* 订单信息 */}
                <Card className="bg-default-50">
                  <CardBody className="p-4">
                    <div className="grid grid-cols-2 gap-3 text-sm">
                      <div>
                        <span className="text-default-500">订单号</span>
                        <p className="font-medium">{orderInfo.order_id}</p>
                      </div>
                      <div>
                        <span className="text-default-500">支付方式</span>
                        <p className="font-medium">{orderInfo.pay_type}</p>
                      </div>
                      <div>
                        <span className="text-default-500">金额</span>
                        <p className="font-medium text-primary">¥{selectedPackage?.price}</p>
                      </div>
                      <div>
                        <span className="text-default-500">渠道</span>
                        <p className="font-medium">{orderInfo.channel}</p>
                      </div>
                    </div>
                  </CardBody>
                </Card>

                {/* 支付状态 */}
                <div className="text-center">
                  {paymentStatus === 'pending' && (
                    <div className="space-y-3">
                      <QrCode size={32} className="mx-auto text-primary" />
                      <p className="text-default-600">请使用微信扫描下方二维码支付</p>
                    </div>
                  )}

                  {paymentStatus === 'checking' && (
                    <div className="space-y-3">
                      <Spinner size="lg" color="primary" />
                      <p className="text-default-600">正在检查支付状态...</p>
                    </div>
                  )}

                  {paymentStatus === 'success' && (
                    <div className="space-y-3">
                      <CheckCircle size={48} className="mx-auto text-success" />
                      <p className="text-success font-medium text-lg">支付成功！</p>
                      <p className="text-default-500 text-sm">页面将自动关闭</p>
                    </div>
                  )}

                  {paymentStatus === 'failed' && (
                    <div className="space-y-3">
                      <AlertCircle size={32} className="mx-auto text-danger" />
                      <p className="text-danger font-medium">支付失败</p>
                      <p className="text-default-500 text-sm">请重试或联系客服</p>
                    </div>
                  )}
                </div>

                {/* 二维码 */}
                {orderInfo.qr_code && paymentStatus === 'pending' && (
                  <div className="flex justify-center">
                    <Card className="p-4">
                      <img 
                        src={generateQRCode(orderInfo.qr_code)} 
                        alt="支付二维码"
                        className="w-48 h-48"
                      />
                    </Card>
                  </div>
                )}
              </div>
            )}
          </ModalBody>

          <ModalFooter>
            {paymentStatus !== 'success' && (
              <Button color="danger" variant="light" onPress={closePaymentModal}>
                取消支付
              </Button>
            )}
          </ModalFooter>
        </ModalContent>
      </Modal>
    </motion.div>
  );
};
