import React, { useState, useEffect } from 'react';
import { Card, CardBody, Button, Chip, Divider } from '@heroui/react';
import { 
  Users, 
  Package, 
  Key, 
  Settings,
  TrendingUp,
  RefreshCw,
  Eye,
  Calendar
} from 'lucide-react';
import { motion } from 'framer-motion';

interface DashboardStats {
  totalUsers: number;
  totalPackages: number;
  totalCDK: number;
  activeUsers: number;
  todayRevenue: number;
  monthlyRevenue: number;
}

const AdminDashboard: React.FC = () => {
  const [stats, setStats] = useState<DashboardStats>({
    totalUsers: 0,
    totalPackages: 0,
    totalCDK: 0,
    activeUsers: 0,
    todayRevenue: 0,
    monthlyRevenue: 0,
  });
  const [loading, setLoading] = useState(true);

  // 模拟数据加载
  useEffect(() => {
    const loadDashboardData = async () => {
      setLoading(true);
      // 模拟API调用延迟
      await new Promise(resolve => setTimeout(resolve, 1000));

      // 模拟数据
      setStats({
        totalUsers: 1248,
        totalPackages: 12,
        totalCDK: 350,
        activeUsers: 89,
        todayRevenue: 2580,
        monthlyRevenue: 45600,
      });

      setLoading(false);
    };

    loadDashboardData();
  }, []);

  const handleRefresh = () => {
    setLoading(true);
    setTimeout(() => setLoading(false), 1000);
  };

  const cardVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { 
      opacity: 1, 
      y: 0,
      transition: { duration: 0.3 }
    }
  };

  const statsCards = [
    {
      title: '总用户数',
      value: stats.totalUsers,
      icon: <Users size={24} />,
      color: '#3b82f6',
      bgColor: '#eff6ff',
      borderColor: '#3b82f6',
    },
    {
      title: '套餐总数',
      value: stats.totalPackages,
      icon: <Package size={24} />,
      color: '#10b981',
      bgColor: '#ecfdf5',
      borderColor: '#10b981',
    },
    {
      title: 'CDK总数',
      value: stats.totalCDK,
      icon: <Key size={24} />,
      color: '#f59e0b',
      bgColor: '#fffbeb',
      borderColor: '#f59e0b',
    },
    {
      title: '活跃用户',
      value: stats.activeUsers,
      icon: <TrendingUp size={24} />,
      color: '#ec4899',
      bgColor: '#fdf2f8',
      borderColor: '#ec4899',
    },
  ];

  const revenueCards = [
    {
      title: '今日收入',
      value: stats.todayRevenue,
      prefix: '¥',
      precision: 2,
      color: '#3b82f6',
    },
    {
      title: '月度收入',
      value: stats.monthlyRevenue,
      prefix: '¥',
      precision: 2,
      color: '#10b981',
    },
  ];

  return (
    <div style={{ padding: '24px', display: 'flex', flexDirection: 'column', gap: '24px' }}>
      {/* 页面标题 */}
      <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
        <div>
          <h2 style={{ 
            margin: 0, 
            color: '#1f2937', 
            fontSize: '24px', 
            fontWeight: 'bold',
            marginBottom: '4px'
          }}>
            仪表盘
          </h2>
          <p style={{ 
            color: '#6b7280', 
            margin: 0, 
            fontSize: '14px' 
          }}>
            欢迎回来，管理员！这里是您的管理概览。
          </p>
        </div>
        <Button
          color="primary"
          startContent={<RefreshCw size={16} />}
          onClick={handleRefresh}
          isLoading={loading}
        >
          刷新数据
        </Button>
      </div>

      {/* 统计卡片 */}
      <div style={{ 
        display: 'grid', 
        gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', 
        gap: '16px' 
      }}>
        {statsCards.map((card, index) => (
          <motion.div
            key={index}
            variants={cardVariants}
            initial="hidden"
            animate="visible"
            transition={{ delay: index * 0.1 }}
          >
            <Card style={{
              backgroundColor: card.bgColor,
              borderLeft: `4px solid ${card.borderColor}`,
            }}>
              <CardBody>
                <div style={{ display: 'flex', alignItems: 'center', justifyContent: 'space-between' }}>
                  <div>
                    <div style={{ 
                      fontSize: '14px', 
                      color: '#6b7280', 
                      marginBottom: '4px' 
                    }}>
                      {card.title}
                    </div>
                    <div style={{ 
                      fontSize: '24px', 
                      fontWeight: 'bold', 
                      color: '#1f2937' 
                    }}>
                      {loading ? '加载中...' : card.value.toLocaleString()}
                    </div>
                  </div>
                  <div style={{ 
                    color: card.color, 
                    opacity: 0.6 
                  }}>
                    {card.icon}
                  </div>
                </div>
              </CardBody>
            </Card>
          </motion.div>
        ))}
      </div>

      {/* 收入统计 */}
      <div style={{ 
        display: 'grid', 
        gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', 
        gap: '16px' 
      }}>
        {revenueCards.map((card, index) => (
          <motion.div
            key={index}
            variants={cardVariants}
            initial="hidden"
            animate="visible"
            transition={{ delay: 0.4 + index * 0.1 }}
          >
            <Card>
              <CardBody>
                <div style={{ textAlign: 'center' }}>
                  <div style={{ 
                    fontSize: '16px', 
                    color: '#6b7280', 
                    marginBottom: '8px' 
                  }}>
                    {card.title}
                  </div>
                  <div style={{ 
                    color: card.color, 
                    fontSize: '28px', 
                    fontWeight: 'bold' 
                  }}>
                    {loading ? '加载中...' : `${card.prefix}${card.value.toFixed(card.precision)}`}
                  </div>
                </div>
              </CardBody>
            </Card>
          </motion.div>
        ))}
      </div>

      {/* 快速操作 */}
      <motion.div
        variants={cardVariants}
        initial="hidden"
        animate="visible"
        transition={{ delay: 0.6 }}
      >
        <Card>
          <CardBody>
            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '8px', 
              marginBottom: '20px' 
            }}>
              <Settings size={18} />
              <span style={{ fontSize: '16px', fontWeight: '500' }}>快速操作</span>
            </div>

            <div style={{ 
              display: 'grid', 
              gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', 
              gap: '16px' 
            }}>
              <Button
                color="primary"
                size="lg"
                fullWidth
                startContent={<Users size={18} />}
                as="a"
                href="/admin/users"
              >
                用户管理
              </Button>
              <Button
                color="primary"
                size="lg"
                fullWidth
                startContent={<Package size={18} />}
                as="a"
                href="/admin/packages"
              >
                套餐管理
              </Button>
              <Button
                color="primary"
                size="lg"
                fullWidth
                startContent={<Key size={18} />}
                as="a"
                href="/admin/cdk"
              >
                CDK管理
              </Button>
              <Button
                color="primary"
                size="lg"
                fullWidth
                startContent={<Settings size={18} />}
                as="a"
                href="/admin/config"
              >
                系统配置
              </Button>
            </div>
          </CardBody>
        </Card>
      </motion.div>

      {/* 系统状态 */}
      <motion.div
        variants={cardVariants}
        initial="hidden"
        animate="visible"
        transition={{ delay: 0.8 }}
      >
        <Card>
          <CardBody>
            <div style={{ 
              display: 'flex', 
              alignItems: 'center', 
              gap: '8px', 
              marginBottom: '20px' 
            }}>
              <Eye size={18} />
              <span style={{ fontSize: '16px', fontWeight: '500' }}>系统状态</span>
            </div>

            <div style={{ 
              display: 'grid', 
              gridTemplateColumns: 'repeat(auto-fit, minmax(200px, 1fr))', 
              gap: '16px',
              marginBottom: '20px'
            }}>
              <div style={{ textAlign: 'center', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '8px' }}>
                <Chip color="success" size="sm" variant="flat">●</Chip>
                <span style={{ fontSize: '14px' }}>API服务正常</span>
              </div>
              <div style={{ textAlign: 'center', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '8px' }}>
                <Chip color="success" size="sm" variant="flat">●</Chip>
                <span style={{ fontSize: '14px' }}>数据库连接正常</span>
              </div>
              <div style={{ textAlign: 'center', display: 'flex', alignItems: 'center', justifyContent: 'center', gap: '8px' }}>
                <Chip color="success" size="sm" variant="flat">●</Chip>
                <span style={{ fontSize: '14px' }}>支付系统正常</span>
              </div>
            </div>

            <Divider />

            <div style={{ 
              textAlign: 'center', 
              color: '#6b7280',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              gap: '8px'
            }}>
              <Calendar size={16} />
              <span style={{ fontSize: '14px' }}>最后更新: {new Date().toLocaleString()}</span>
            </div>
          </CardBody>
        </Card>
      </motion.div>
    </div>
  );
};

export default AdminDashboard;
