/**
 * 订单记录Tab页面
 * 显示用户的历史订单记录
 */
import React, { useState, useEffect } from 'react';
import { Card, CardBody, Button, Chip, Input, Spinner } from '@heroui/react';
import { motion } from 'framer-motion';
import { 
  FileText, 
  Search, 
  Calendar, 
  CheckCircle, 
  Clock, 
  XCircle,
  RefreshCw,
  AlertCircle
} from 'lucide-react';
import { orderUserApi } from '../../../services/userApi';

interface OrderInfo {
  order_id: string;
  package_id: number;
  package_name: string;
  status: string;
  created_at: string;
}

export const OrderHistoryTab: React.FC = () => {
  const [orders, setOrders] = useState<OrderInfo[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string>('');
  const [searchTerm, setSearchTerm] = useState<string>('');

  // 获取订单列表
  const fetchOrders = async () => {
    try {
      setLoading(true);
      setError('');
      const response = await orderUserApi.getOrders();

      if (response.code === 20000) {
        setOrders(response.data || []);
      } else {
        setError(response.msg || '获取订单记录失败');
      }
    } catch (err) {
      setError(err instanceof Error ? err.message : '网络错误');
    } finally {
      setLoading(false);
    }
  };

  // 过滤订单
  const filteredOrders = orders.filter(order =>
    order.order_id.toLowerCase().includes(searchTerm.toLowerCase()) ||
    order.package_name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  // 获取订单状态配置
  const getOrderStatusConfig = (status: string) => {
    switch (status) {
      case '已支付':
      case '已完成':
      case 'completed':
        return { 
          color: 'success' as const, 
          icon: CheckCircle,
          text: '已完成'
        };
      case '待支付':
      case 'pending':
        return { 
          color: 'warning' as const, 
          icon: Clock,
          text: '待支付'
        };
      case '已取消':
      case '支付失败':
      case 'failed':
      case 'cancelled':
        return { 
          color: 'danger' as const, 
          icon: XCircle,
          text: '已取消'
        };
      default:
        return { 
          color: 'default' as const, 
          icon: RefreshCw,
          text: status
        };
    }
  };

  // 统计数据
  const getOrderStats = () => {
    const total = orders.length;
    const completed = orders.filter(order => 
      ['已支付', '已完成', 'completed'].includes(order.status)
    ).length;
    const pending = orders.filter(order => 
      ['待支付', 'pending'].includes(order.status)
    ).length;
    const failed = orders.filter(order => 
      ['已取消', '支付失败', 'failed', 'cancelled'].includes(order.status)
    ).length;

    return { total, completed, pending, failed };
  };

  const stats = getOrderStats();

  useEffect(() => {
    fetchOrders();
  }, []);

  return (
    <motion.div
      initial={{ opacity: 0 }}
      animate={{ opacity: 1 }}
      className="space-y-6"
    >
      {/* 页面标题 */}
      <div className="flex items-center gap-3">
        <div className="w-8 h-8 bg-primary/10 rounded-lg flex items-center justify-center">
          <FileText size={20} className="text-primary" />
        </div>
        <div>
          <h1 className="text-2xl font-bold text-default-900">订单记录</h1>
          <p className="text-sm text-default-500 mt-1">查看您的历史订单和交易记录</p>
        </div>
      </div>

      {/* 筛选工具栏 */}
      <Card>
        <CardBody className="p-6">
          <div className="flex flex-col sm:flex-row gap-4">
            <div className="flex-1">
              <Input
                placeholder="搜索订单号、套餐名称..."
                startContent={<Search size={16} />}
                variant="bordered"
                value={searchTerm}
                onValueChange={setSearchTerm}
              />
            </div>
            <div className="flex gap-2">
              <Button 
                variant="bordered" 
                startContent={<RefreshCw size={16} />}
                onPress={fetchOrders}
                isLoading={loading}
              >
                刷新
              </Button>
            </div>
          </div>
        </CardBody>
      </Card>

      {/* 订单统计 */}
      <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-4 gap-4">
        <Card>
          <CardBody className="p-4 text-center">
            <div className="text-2xl font-bold text-primary mb-1">{stats.total}</div>
            <div className="text-sm text-default-500">总订单数</div>
          </CardBody>
        </Card>
        <Card>
          <CardBody className="p-4 text-center">
            <div className="text-2xl font-bold text-success mb-1">{stats.completed}</div>
            <div className="text-sm text-default-500">已完成</div>
          </CardBody>
        </Card>
        <Card>
          <CardBody className="p-4 text-center">
            <div className="text-2xl font-bold text-warning mb-1">{stats.pending}</div>
            <div className="text-sm text-default-500">待支付</div>
          </CardBody>
        </Card>
        <Card>
          <CardBody className="p-4 text-center">
            <div className="text-2xl font-bold text-danger mb-1">{stats.failed}</div>
            <div className="text-sm text-default-500">已取消</div>
          </CardBody>
        </Card>
      </div>

      {/* 加载状态 */}
      {loading && (
        <Card>
          <CardBody className="p-6">
            <div className="flex items-center justify-center py-12">
              <Spinner size="lg" color="primary" />
              <span className="ml-3 text-default-600">加载中...</span>
            </div>
          </CardBody>
        </Card>
      )}

      {/* 错误状态 */}
      {error && (
        <Card>
          <CardBody className="p-6">
            <div className="flex items-start gap-4 p-6 bg-danger/10 rounded-lg">
              <AlertCircle size={20} className="text-danger flex-shrink-0 mt-1" />
              <div>
                <h3 className="font-semibold text-danger mb-2">加载失败</h3>
                <p className="text-default-600 text-sm">{error}</p>
                <button
                  onClick={fetchOrders}
                  className="mt-3 text-sm text-primary hover:text-primary-600 underline"
                >
                  重试
                </button>
              </div>
            </div>
          </CardBody>
        </Card>
      )}

      {/* 订单列表 */}
      {!loading && !error && (
        <>
          {filteredOrders.length > 0 ? (
            <div className="space-y-4">
              {filteredOrders.map((order, index) => {
                const statusConfig = getOrderStatusConfig(order.status);
                const StatusIcon = statusConfig.icon;

                return (
                  <motion.div
                    key={order.order_id}
                    initial={{ opacity: 0, y: 20 }}
                    animate={{ opacity: 1, y: 0 }}
                    transition={{ delay: index * 0.05 }}
                  >
                    <Card className="hover:shadow-md transition-shadow">
                      <CardBody className="p-6">
                        <div className="flex flex-col lg:flex-row items-start lg:items-center gap-4">
                          {/* 订单基本信息 */}
                          <div className="flex-1 space-y-2">
                            <div className="flex items-center gap-3 flex-wrap">
                              <h3 className="font-semibold text-default-900 break-all">
                                {order.order_id}
                              </h3>
                              <Chip 
                                size="sm" 
                                color={statusConfig.color}
                                variant="flat"
                                startContent={<StatusIcon size={14} />}
                              >
                                {statusConfig.text}
                              </Chip>
                            </div>

                            <div className="grid grid-cols-1 sm:grid-cols-2 gap-4 text-sm">
                              <div className="flex items-center gap-2 text-default-600">
                                <Calendar size={16} />
                                <span>{order.created_at}</span>
                              </div>
                              <div className="font-medium text-default-900">
                                {order.package_name}
                              </div>
                            </div>
                          </div>

                          {/* 操作按钮 */}
                          <div className="flex flex-col gap-2">
                            <Button size="sm" variant="bordered">
                              查看详情
                            </Button>
                            {statusConfig.color === 'success' && (
                              <Button size="sm" color="primary" variant="light">
                                重新购买
                              </Button>
                            )}
                          </div>
                        </div>
                      </CardBody>
                    </Card>
                  </motion.div>
                );
              })}
            </div>
          ) : (
            <div className="text-center py-12 text-default-400">
              <FileText size={48} className="mx-auto mb-4 opacity-50" />
              <p>
                {searchTerm ? '没有找到匹配的订单' : '暂无订单记录'}
              </p>
              {searchTerm && (
                <Button
                  variant="light"
                  size="sm"
                  className="mt-2"
                  onPress={() => setSearchTerm('')}
                >
                  清除搜索
                </Button>
              )}
            </div>
          )}
        </>
      )}
    </motion.div>
  );
};
