import React, { useState, useEffect } from 'react';
import { 
  Card, 
  Button,
  Input,
  Select,
  SelectItem,
  Chip,
  Avatar
} from '@heroui/react';
import { RefreshCw, Search, User, Package } from 'lucide-react';
import { userPackageApi } from '../../services/api';
import { motion } from 'framer-motion';

interface UserPackage {
  id: number;
  user_id: number;
  username: string;
  email?: string;
  package_id: number;
  package_name: string;
  category: string;
  price: number;
  duration: number;
  start_date: string;
  end_date: string;
  status: number;
  created_at: string;
  updated_at?: string;
}

const AdminUserPackages: React.FC = () => {
  const [data, setData] = useState<UserPackage[]>([]);
  const [loading, setLoading] = useState(false);

  // 搜索参数
  const [searchParams, setSearchParams] = useState({
    username: '',
    package_name: '',
    category: '',
    status: '',
  });

  // 加载数据
  const loadData = async () => {
    setLoading(true);
    try {
      const requestParams = {
        page: 1,
        limit: 50,
        ...searchParams,
      };

      const result = await userPackageApi.list(requestParams);

      setData(Array.isArray(result.data) ? result.data : []);
    } catch (error: any) {
      console.error('加载数据失败:', error.message);
      setData([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadData();
  }, []);

  // 搜索
  const handleSearch = () => {
    loadData();
  };

  // 重置搜索
  const handleReset = () => {
    setSearchParams({
      username: '',
      package_name: '',
      category: '',
      status: '',
    });
    setTimeout(() => loadData(), 100);
  };

  // 获取状态标签
  const getStatusTag = (status: number, endDate: string) => {
    const now = new Date();
    const end = new Date(endDate);

    if (status === 0) {
      return <Chip color="danger" variant="flat" size="sm">已禁用</Chip>;
    }

    if (end < now) {
      return <Chip color="default" variant="flat" size="sm">已过期</Chip>;
    }

    const daysDiff = Math.ceil((end.getTime() - now.getTime()) / (1000 * 60 * 60 * 24));
    if (daysDiff <= 7) {
      return <Chip color="warning" variant="flat" size="sm">即将过期</Chip>;
    }

    return <Chip color="success" variant="flat" size="sm">正常</Chip>;
  };

  const getCategoryColor = (category: string) => {
    const colorMap: { [key: string]: any } = {
      '个人': 'primary',
      '企业': 'success',
      '高级': 'secondary',
    };
    return colorMap[category] || 'default';
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.3 }}
      className="p-6"
    >
      <Card className="w-full">
        {/* 页面标题 */}
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-2xl font-bold">用户套餐</h2>
          <Button
            variant="flat"
            startContent={<RefreshCw className="w-4 h-4" />}
            onClick={loadData}
            isLoading={loading}
          >
            刷新
          </Button>
        </div>

        {/* 搜索表单 */}
        <Card className="p-4 mb-6">
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
            <Input
              label="用户名"
              placeholder="请输入用户名"
              value={searchParams.username}
              onChange={(e) => setSearchParams({
                ...searchParams,
                username: e.target.value
              })}
            />

            <Input
              label="套餐名称"
              placeholder="请输入套餐名称"
              value={searchParams.package_name}
              onChange={(e) => setSearchParams({
                ...searchParams,
                package_name: e.target.value
              })}
            />

            <Select
              label="套餐分类"
              placeholder="请选择分类"
              selectedKeys={searchParams.category ? [searchParams.category] : []}
              onSelectionChange={(keys) => {
                const value = Array.from(keys)[0] as string || '';
                setSearchParams({
                  ...searchParams,
                  category: value
                });
              }}
            >
              <SelectItem key="个人">个人</SelectItem>
              <SelectItem key="企业">企业</SelectItem>
              <SelectItem key="高级">高级</SelectItem>
            </Select>

            <Select
              label="状态"
              placeholder="请选择状态"
              selectedKeys={searchParams.status ? [searchParams.status] : []}
              onSelectionChange={(keys) => {
                const value = Array.from(keys)[0] as string || '';
                setSearchParams({
                  ...searchParams,
                  status: value
                });
              }}
            >
              <SelectItem key="1">正常</SelectItem>
              <SelectItem key="0">禁用</SelectItem>
            </Select>
          </div>

          <div className="flex justify-end gap-2 mt-4">
            <Button variant="flat" onClick={handleReset}>
              重置
            </Button>
            <Button 
              color="primary" 
              startContent={<Search className="w-4 h-4" />}
              onClick={handleSearch}
              isLoading={loading}
            >
              搜索
            </Button>
          </div>
        </Card>

        {/* 简化的数据展示 */}
        <div className="space-y-4">
          {loading ? (
            <div className="text-center py-8">加载中...</div>
          ) : data.length === 0 ? (
            <div className="text-center py-8 text-gray-500">暂无数据</div>
          ) : (
            <div className="grid gap-4">
              {data.map((userPackage) => (
                <Card key={userPackage.id} className="p-4">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-6">
                      <div className="flex items-center gap-3">
                        <Avatar 
                          icon={<User className="w-4 h-4" />}
                          size="sm"
                        />
                        <div>
                          <div className="font-medium">{userPackage.username}</div>
                          {userPackage.email && (
                            <div className="text-xs text-gray-500">{userPackage.email}</div>
                          )}
                        </div>
                      </div>

                      <div className="flex items-center gap-3">
                        <Package className="w-5 h-5 text-blue-500" />
                        <div>
                          <div className="font-medium">{userPackage.package_name}</div>
                          <div className="text-xs text-gray-500">
                            ¥{userPackage.price.toFixed(2)} · {userPackage.duration}天
                          </div>
                        </div>
                      </div>

                      <div className="text-sm text-gray-600">
                        <div>开始: {new Date(userPackage.start_date).toLocaleDateString()}</div>
                        <div>结束: {new Date(userPackage.end_date).toLocaleDateString()}</div>
                      </div>
                    </div>

                    <div className="flex items-center gap-4">
                      <Chip 
                        color={getCategoryColor(userPackage.category)} 
                        variant="flat"
                        size="sm"
                      >
                        {userPackage.category}
                      </Chip>
                      {getStatusTag(userPackage.status, userPackage.end_date)}
                      <div className="text-xs text-gray-500">
                        购买时间: {new Date(userPackage.created_at).toLocaleDateString()}
                      </div>
                    </div>
                  </div>
                </Card>
              ))}
            </div>
          )}
        </div>
      </Card>
    </motion.div>
  );
};

export default AdminUserPackages;
