      opacity: 0,
    }),
    center: {
      x: 0,
      opacity: 1,
    },
    exit: (direction: string) => ({
      x: direction === 'next' ? -30 : 30,
      opacity: 0,
    }),
  },
  backgroundVariants: {
    enter: {
      opacity: 0,
      scale: 1.01,
    },
    center: {
      opacity: 1,
      scale: 1,
    },
    exit: {
      opacity: 0,
      scale: 0.99,
    },
  },
};

