import React, { useState, useEffect } from 'react';
import { 
  Card, 
  Button, 
  Modal, 
  ModalContent, 
  ModalHeader, 
  ModalBody, 
  ModalFooter,
  Input,
  Textarea,
  Select,
  SelectItem,
  Chip,
  useDisclosure
} from '@heroui/react';
import { Plus, Edit, Trash2, RefreshCw, Key } from 'lucide-react';
import { cdkApi } from '../../services/api';
import { motion } from 'framer-motion';

interface CDK {
  id: number;
  code: string;
  package_id: number;
  package_name?: string;
  category?: string;
  price?: number;
  duration?: number;
  status: number;
  used_by?: number;
  used_by_username?: string;
  used_at?: string;
  created_at?: string;
  expires_at?: string;
  remarks?: string;
}

const AdminCDK: React.FC = () => {
  const [data, setData] = useState<CDK[]>([]);
  const [loading, setLoading] = useState(false);
  const [editingRecord, setEditingRecord] = useState<CDK | null>(null);
  const { isOpen, onOpen, onOpenChange } = useDisclosure();

  // 表单数据
  const [formData, setFormData] = useState({
    code: '',
    package_id: '1',
    status: '1',
    expires_at: '',
    remarks: ''
  });

  // 加载数据
  const loadData = async () => {
    setLoading(true);
    try {
      const result = await cdkApi.list({
        page: 1,
        limit: 50,
      });

      setData(Array.isArray(result.data) ? result.data : []);
    } catch (error: any) {
      console.error('加载数据失败:', error.message);
      setData([]);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    loadData();
  }, []);

  // 生成随机CDK码
  const generateCode = () => {
    const chars = 'ABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789';
    let result = '';
    for (let i = 0; i < 16; i++) {
      if (i > 0 && i % 4 === 0) result += '-';
      result += chars.charAt(Math.floor(Math.random() * chars.length));
    }
    return result;
  };

  // 新增CDK
  const handleAdd = () => {
    setEditingRecord(null);
    const tomorrow = new Date();
    tomorrow.setDate(tomorrow.getDate() + 30);

    setFormData({
      code: generateCode(),
      package_id: '1',
      status: '1',
      expires_at: tomorrow.toISOString().split('T')[0],
      remarks: ''
    });
    onOpen();
  };

  // 编辑CDK
  const handleEdit = (record: CDK) => {
    setEditingRecord(record);
    setFormData({
      code: record.code,
      package_id: String(record.package_id),
      status: String(record.status),
      expires_at: record.expires_at ? record.expires_at.split('T')[0] : '',
      remarks: record.remarks || ''
    });
    onOpen();
  };

  // 删除CDK
  const handleDelete = async (id: number) => {
    if (!confirm('确定删除这个CDK吗？')) return;

    try {
      await cdkApi.delete(id);
      alert('删除成功');
      loadData();
    } catch (error: any) {
      alert(error.message || '删除失败');
    }
  };

  // 保存CDK
  const handleSave = async () => {
    try {
      const saveData = {
        ...formData,
        package_id: parseInt(formData.package_id),
        status: parseInt(formData.status),
        expires_at: formData.expires_at ? new Date(formData.expires_at).toISOString() : null
      };

      if (editingRecord) {
        await cdkApi.update({ ...editingRecord, ...saveData });
        alert('更新成功');
      } else {
        await cdkApi.create(saveData);
        alert('创建成功');
      }

      onOpenChange();
      loadData();
    } catch (error: any) {
      alert(error.message || '保存失败');
    }
  };

  // 获取状态标签
  const getStatusTag = (status: number, expiresAt?: string) => {
    if (status === 0) {
      return <Chip color="danger" variant="flat" size="sm">已禁用</Chip>;
    }

    if (status === 2) {
      return <Chip color="success" variant="flat" size="sm">已使用</Chip>;
    }

    if (expiresAt && new Date(expiresAt) < new Date()) {
      return <Chip color="default" variant="flat" size="sm">已过期</Chip>;
    }

    return <Chip color="primary" variant="flat" size="sm">可用</Chip>;
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      transition={{ duration: 0.3 }}
      className="p-6"
    >
      <Card className="w-full">
        {/* 页面标题 */}
        <div className="flex items-center justify-between mb-6">
          <h2 className="text-2xl font-bold">CDK管理</h2>
          <div className="flex gap-2">
            <Button
              variant="flat"
              startContent={<RefreshCw className="w-4 h-4" />}
              onClick={loadData}
              isLoading={loading}
            >
              刷新
            </Button>
            <Button
              color="primary"
              startContent={<Plus className="w-4 h-4" />}
              onClick={handleAdd}
            >
              生成CDK
            </Button>
          </div>
        </div>

        {/* 简化的数据展示 */}
        <div className="space-y-4">
          {loading ? (
            <div className="text-center py-8">加载中...</div>
          ) : data.length === 0 ? (
            <div className="text-center py-8 text-gray-500">暂无数据</div>
          ) : (
            <div className="grid gap-4">
              {data.map((cdk) => (
                <Card key={cdk.id} className="p-4">
                  <div className="flex items-center justify-between">
                    <div className="flex items-center gap-4">
                      <Key className="w-8 h-8 text-blue-500" />
                      <div>
                        <div className="font-mono text-lg font-medium">{cdk.code}</div>
                        <div className="text-sm text-gray-500 mt-1">
                          套餐: {cdk.package_name || '未关联'} · 价格: ¥{(cdk.price || 0).toFixed(2)}
                        </div>
                        {cdk.used_by_username && (
                          <div className="text-sm text-gray-400 mt-1">
                            使用者: {cdk.used_by_username} · 使用时间: {cdk.used_at ? new Date(cdk.used_at).toLocaleDateString() : '-'}
                          </div>
                        )}
                        <div className="text-sm text-gray-400 mt-1">
                          过期时间: {cdk.expires_at ? new Date(cdk.expires_at).toLocaleDateString() : '-'}
                        </div>
                      </div>
                    </div>
                    <div className="flex items-center gap-4">
                      {cdk.category && (
                        <Chip color="secondary" variant="flat" size="sm">
                          {cdk.category}
                        </Chip>
                      )}
                      {getStatusTag(cdk.status, cdk.expires_at)}
                      <div className="flex gap-2">
                        <Button
                          size="sm"
                          variant="flat"
                          isIconOnly
                          onClick={() => handleEdit(cdk)}
                        >
                          <Edit className="w-4 h-4" />
                        </Button>
                        <Button
                          size="sm"
                          variant="flat"
                          color="danger"
                          isIconOnly
                          onClick={() => handleDelete(cdk.id)}
                        >
                          <Trash2 className="w-4 h-4" />
                        </Button>
                      </div>
                    </div>
                  </div>
                </Card>
              ))}
            </div>
          )}
        </div>
      </Card>

      {/* 编辑/新增模态框 */}
      <Modal isOpen={isOpen} onOpenChange={onOpenChange} size="2xl">
        <ModalContent>
          {(onClose) => (
            <>
              <ModalHeader>
                {editingRecord ? '编辑CDK' : '生成CDK'}
              </ModalHeader>
              <ModalBody>
                <div className="space-y-4">
                  <div className="flex gap-2">
                    <Input
                      label="CDK码"
                      placeholder="请输入CDK码"
                      value={formData.code}
                      onChange={(e) => setFormData({...formData, code: e.target.value})}
                      isRequired
                      className="flex-1"
                    />
                    <Button
                      variant="flat"
                      onClick={() => setFormData({...formData, code: generateCode()})}
                      className="mt-6"
                    >
                      重新生成
                    </Button>
                  </div>

                  <Select
                    label="关联套餐"
                    placeholder="请选择关联套餐"
                    selectedKeys={[formData.package_id]}
                    onSelectionChange={(keys) => {
                      const value = Array.from(keys)[0] as string;
                      setFormData({...formData, package_id: value});
                    }}
                  >
                    <SelectItem key="1">个人套餐 - 月卡</SelectItem>
                    <SelectItem key="2">企业套餐 - 年卡</SelectItem>
                    <SelectItem key="3">高级套餐 - 季卡</SelectItem>
                  </Select>

                  <div className="grid grid-cols-2 gap-4">
                    <Select
                      label="状态"
                      placeholder="请选择状态"
                      selectedKeys={[formData.status]}
                      onSelectionChange={(keys) => {
                        const value = Array.from(keys)[0] as string;
                        setFormData({...formData, status: value});
                      }}
                    >
                      <SelectItem key="1">可用</SelectItem>
                      <SelectItem key="0">禁用</SelectItem>
                    </Select>

                    <Input
                      label="过期时间"
                      placeholder="请选择过期时间"
                      type="date"
                      value={formData.expires_at}
                      onChange={(e) => setFormData({...formData, expires_at: e.target.value})}
                      isRequired
                    />
                  </div>

                  <Textarea
                    label="备注"
                    placeholder="请输入备注"
                    value={formData.remarks}
                    onChange={(e) => setFormData({...formData, remarks: e.target.value})}
                    maxRows={3}
                  />
                </div>
              </ModalBody>
              <ModalFooter>
                <Button variant="flat" onPress={onClose}>
                  取消
                </Button>
                <Button color="primary" onPress={() => { handleSave(); onClose(); }}>
                  保存
                </Button>
              </ModalFooter>
            </>
          )}
        </ModalContent>
      </Modal>
    </motion.div>
  );
};

export default AdminCDK;
